utterance.field:
//This is identical to the intro that plays when the Toolkit starts.  That intro is kept in Main.cs, a Unity script

//Start NPCEditor Scripting
engine.begin();

//These lines let NPCEditor know who is present in a scene
engine.context.presentCharacters.add("Brad");
engine.context.presentCharacters.add("Rachel");

//Engine.send is a script command that sends a vhmsg from within NPCEditor.  These lines set the posture for NVBG
engine.send('vrExpress Brad ALL sbm_gs-all_test_bml_7 <?xml version="1.0" encoding="UTF-8"?> <act> <bml> <body posture="ChrBrad@Idle01"/> </bml> </act>');
engine.send('vrExpress Rachel ALL sbm_gs-all_test_bml_8 <?xml version="1.0" encoding="UTF-8"?> <act> <bml> <body posture="ChrRachel_ChrBrad@Idle01"/> </bml> </act>');

//Engine.schedule plays back a line of dialog from within the plist, using the External ID
engine.schedule("rachel_hirachel");
engine.schedule("brad_hibrad");
engine.schedule("rachel_charlikeus");
engine.schedule("brad_showoff");
engine.schedule("brad_ourselves");
engine.schedule("rachel_generalqs");

//This lets NPCEditor know a script has finished
engine.end();

//This is an alternate intro that uses vrExpress and TTS, instead of dialog within the plist.

engine.begin();

engine.context.presentCharacters.add("Brad");
engine.context.presentCharacters.add("Rachel");

engine.send('vrExpress Brad ALL sbm_gs-all_test_bml_7 <?xml version="1.0" encoding="UTF-8"?> <act> <bml> <body posture="ChrBrad@Idle01"/> </bml> </act>');
engine.send('vrExpress Rachel ALL sbm_gs-all_test_bml_8 <?xml version="1.0" encoding="UTF-8"?> <act> <bml> <body posture="ChrRachel_ChrBrad@Idle01"/> </bml> </act>');

//These vrExpress messages are received by SmartBody and the TTS system
engine.send('vrExpress Brad user 1303332588320-112-1 <?xml version="1.0" encoding="UTF-8" standalone="no"?><act><participant id="brad" role="actor" /><fml><turn start="take" end="give" /><affect type="neutral" target="addressee"></affect></fml><bml><speech id="sp3" ref="vrintro1" type="application/ssml+xml">Hello and welcome to the VHTK  I am Brad</speech></bml></act>');

//Telling NPCEditor to wait X seconds before sending the next command.
engine.rest(4);

engine.send('vrExpress Rachel user 1303332588320-122-1 <?xml version="1.0" encoding="UTF-8" standalone="no" ?><act><participant id="brad" role="actor" /><fml><turn start="take" end="give" /><affect type="neutral" target="addressee"></affect></fml><bml><speech id="sp2" ref="vrintro2" type="application/ssml+xml">And I am Rachel.  We are virtual humans.</speech></bml></act>');

engine.end();

//This is an example of a simple script that has both Brad and Rachel answer a question.  It is linked as if a normal //answer.  These kind of scripts can contain dialog, custom animation calls, NVBG control commands, or any other vhMSG

engine.begin();

engine.schedule("rachel_peoplecreate");
engine.schedule("brad_uscict");

engine.end();

engine.begin();

engine.schedule("brad_cantsee");
engine.schedule("rachel_watson");
engine.schedule("brad_webcam");
engine.schedule("rachel_watsondemo");

engine.end();
